mapper.select:
select count(*)
from moment_like
where moment_id=#{momentId}

select moment_like_id, moment_id,
       ml.user_id, username, email 'user_email', mobile 'user_mobile', time
from moment_like ml join user u on ml.user_id = u.user_id
where moment_id=#{momentId}
order by time desc

  limit #{starting}, #{pageSize}
