mapper.update:
update moment set
  content=#{content}
where moment_id=#{momentId}

mapper.select:
select moment_id, enterprise_id, content, time
from moment
where moment_id=#{momentId}

select count(*)
from moment
where enterprise_id=#{enterpriseId}

select moment_id, enterprise_id, content, time
from moment
where enterprise_id=#{enterpriseId}
order by time desc

  limit #{starting}, #{pageSize}
